import { useState, useEffect } from "react";

const ImageSlider = ({ images, children, onImageClick }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    if (currentIndex < 0) {
      setCurrentIndex(images.length - 1);
    } else if (currentIndex >= images.length) {
      setCurrentIndex(0);
    }
  }, [currentIndex, images]);

  const sliderStyles = {
    position: "relative",
    ...containerStyles,
  };

  const slideStyles = {
    width: "100%",
    height: "100%",
    borderRadius: "1em",
    border: "solid 5px",
    borderColor: "rgb(28, 28, 28)",
    boxShadow: "0 5px 0 rgb(45, 45, 61)",
    backgroundPosition: "center",
    backgroundSize: "cover",
    backgroundImage: `url(${images[currentIndex].url})`,
  };

  const leftArrowStyles = {
    position: "absolute",
    top: "50%",
    transform: "translate(0,-50%)",
    left: "-30px",
    fontSize: "45px",
    color: "rgb(210, 210, 255)",
    zIndex: 1,
    cursor: "pointer",
    fontWeight: "bold",
    textShadow:
      "-2px -2px 0 rgb(28, 28, 28), 2px -2px 0 rgb(28, 28, 28), -2px 2px 0 rgb(28, 28, 28), 2px 2px 0 rgb(28, 28, 28)",
  };

  const rightArrowStyles = {
    position: "absolute",
    top: "50%",
    transform: "translate(0,-50%)",
    right: "-30px",
    fontSize: "45px",
    color: "rgb(210, 210, 255)",
    zIndex: 1,
    cursor: "pointer",
    fontWeight: "bold",
    textShadow:
      "-2px -2px 0 rgb(28, 28, 28), 2px -2px 0 rgb(28, 28, 28), -2px 2px 0 rgb(28, 28, 28), 2px 2px 0 rgb(28, 28, 28)",
  };

  const dotsContainerStyles = {
    display: "flex",
    justifyContent: "center",
  };

  const dotStyles = {
    margin: "0 3px",
    cursor: "pointer",
    fontSize: "30px",
    fontWeight: "bold",
    color: "rgb(210, 210, 255)",
    textShadow:
      "-2px -2px 0 rgb(28, 28, 28), 2px -2px 0 rgb(28, 28, 28), -2px 2px 0 rgb(28, 28, 28), 2px 2px 0 rgb(28, 28, 28)",
  };

  const goToPrevious = () => {
    const isFirstImage = currentIndex === 0;
    const newIndex = isFirstImage ? images.length - 1 : currentIndex - 1;
    setCurrentIndex(newIndex);
  };

  const goToNext = () => {
    const isLastSlide = currentIndex === images.length - 1;
    const newIndex = isLastSlide ? 0 : currentIndex + 1;
    setCurrentIndex(newIndex);
  };

  const goToSlide = (imageIndex) => {
    setCurrentIndex(imageIndex);
  };

  return (
    <div style={sliderStyles}>
      <div style={leftArrowStyles} onClick={goToPrevious}>
        &lt;
      </div>
      <div style={rightArrowStyles} onClick={goToNext}>
        &gt;
      </div>
      <div style={slideStyles}>
        {children && (
          <div
            style={{
              position: "absolute",
              top: "50%",
              left: "50%",
              transform: "translate(-50%, -50%)",
            }}
          >
            <div>
              style={slideStyles} onClick={() => onImageClick(currentIndex)}
              {children}
            </div>
          </div>
        )}
      </div>
      <div style={dotsContainerStyles}>
        {images.map((image, slideIndex) => (
          <div style={slideStyles} onClick={() => onImageClick(currentIndex)}>
            &#x2022;
          </div>
        ))}
      </div>
    </div>
  );
};

export default ImageSlider;
