import { useState } from "react";

const AddZines = ({ onAdd }) => {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [description, setDescription] = useState("");
  const [images, setImages] = useState([]);

  const onSubmit = async (e) => {
    e.preventDefault();

    // Assuming you have a server endpoint to handle file uploads.
    // This is an example, and you need to replace it with your actual endpoint.
    const uploadedImageUrls = await Promise.all(
      images.map(async (file) => {
        const formData = new FormData();
        formData.append("image", file);

        const response = await fetch("http://localhost:7000/zines", {
          method: "POST",
          body: formData,
        });

        if (response.ok) {
          const imageUrl = await response.json();
          return imageUrl;
        } else {
          throw new Error("Image upload failed");
        }
      })
    );

    onAdd({ title, author, description, images: uploadedImageUrls });

    setTitle("");
    setAuthor("");
    setDescription("");
    setImages([]);
  };

  const handleImageChange = (e) => {
    setImages(e.target.files);
  };

  return (
    <form className="add-zine" onSubmit={onSubmit}>
      <label htmlFor="title">Title:</label>
      <br />
      <input
        type="text"
        id="title"
        name="title"
        placeholder="Title of this zine"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
      ></input>
      <br />
      <label htmlFor="author">Author:</label>
      <br />
      <input
        type="text"
        id="author"
        name="author"
        placeholder="Author of this zine"
        value={author}
        onChange={(e) => setAuthor(e.target.value)}
        required
      ></input>
      <br />
      <label htmlFor="description">Description:</label>
      <br />
      <textarea
        id="description"
        name="description"
        placeholder="Add a description for your zine here"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      ></textarea>
      <br />
      <label htmlFor="images">Images:</label>
      <br />
      <input
        type="file"
        id="images"
        name="images"
        accept="image/*"
        onChange={handleImageChange}
        multiple
        required
      ></input>
      <br />
      <input type="submit" value="Upload Zine"></input>
    </form>
  );
};

export default AddZines;
