import { useState } from "react";

const AddZines = ({ onAdd }) => {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [description, setDescription] = useState("");
  const [images, setImages] = useState([]);

  const onSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:3000/zines", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          title,
          author,
          description,
          images,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        onAdd(data);
      } else {
        console.error("Error uploading zine:", response.statusText);
      }

      setTitle("");
      setAuthor("");
      setDescription("");
      setImages([]);
    } catch (error) {
      console.error("Error uploading zine:", error);
    }
  };

  const handleImageChange = (e) => {
    const files = e.target.files;
    if (files && files.length > 0) {
      const imagePreviews = Array.from(files).map((file) =>
        URL.createObjectURL(file)
      );

      setImages(imagePreviews);
    }
  };

  return (
    <form className="add-zine" onSubmit={onSubmit}>
      <label htmlFor="title">Title:</label>
      <br />
      <input
        type="text"
        id="title"
        name="title"
        placeholder="Title of this zine"
        value={title}
        onChange={handleImageChange}
        multiple
        required
      ></input>
      <br />
      <label htmlFor="author">Author:</label>
      <br />
      <input
        type="text"
        id="author"
        name="author"
        placeholder="Author of this zine"
        value={author}
        onChange={(e) => setAuthor(e.target.value)}
        required
      ></input>
      <br />
      <label htmlFor="description">Description:</label>
      <br />
      <textarea
        id="description"
        name="description"
        placeholder="Add a description for your zine here"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      ></textarea>
      <br />
      <label htmlFor="images">Images:</label>
      <br />
      <input
        type="file"
        id="images"
        name="images"
        accept="image/*"
        onChange={(e) => setImages(e.target.files)}
        multiple
        required
      ></input>
      <br />
      <input type="submit" value="Upload Zine"></input>
    </form>
  );
};

export default AddZines;
