import { useLocation } from "react-router-dom";
import Button from "./Button";

const ZineButton = ({ title, onAdd, showAdd }) => {
  return (
    <div>
      <h1>{title}</h1>
      <Button
        color={showAdd ? "red" : "green"}
        text={showAdd ? "Close" : "Upload a Zine"}
        onClick={onAdd}
      />
    </div>
  );
};

export default ZineButton;
