import { useState } from "react";

const AddZines = ({ onAdd }) => {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [description, setDescription] = useState("");
  const [images, setImages] = useState([]);

  const onSubmit = (e) => {
    e.preventDefault();

    if (!Array.isArray(images)) {
      // If not an array, convert to array
      const imageArray = [images];
      onAdd({ title, author, description, images: imageArray });
    } else {
      onAdd({ title, author, description, images });
    }

    const imageUrls = images.map((image) => URL.createObjectURL(image));

    onAdd({ title, author, description, images: imageUrls });

    setTitle("");
    setAuthor("");
    setDescription("");
    setImages([]);
  };

  const handleImageChange = (e) => {
    const files = e.target.files;

    setImages(files);
  };

  return (
    <form className="add-zine" onSubmit={onSubmit}>
      <label htmlFor="title">Title:</label>
      <br />
      <input
        type="text"
        id="title"
        name="title"
        placeholder="Title of this zine"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
      ></input>
      <br />
      <label htmlFor="author">Author:</label>
      <br />
      <input
        type="text"
        id="author"
        name="author"
        placeholder="Author of this zine"
        value={author}
        onChange={(e) => setAuthor(e.target.value)}
        required
      ></input>
      <br />
      <label htmlFor="description">Description:</label>
      <br />
      <textarea
        id="description"
        name="description"
        placeholder="Add a description for your zine here"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      ></textarea>
      <br />
      <label htmlFor="images">Images:</label>
      <br />
      <input
        type="file"
        id="images"
        name="images"
        accept="image/*"
        onChange={handleImageChange}
        multiple
        required
      ></input>
      <br />
      <input type="submit" value="Upload Zine"></input>
    </form>
  );
};

export default AddZines;
