    <div>
      {zines.map((zine, index) => (
        <ReactCardFlip
          key={index}
          flipDirection="horizontal"
          isFlipped={flippedCards[index] || false}
        >
          <div key="front" onClick={() => flipCard(index)}>
            {/* Content for the front of the card */}
            Click to flip
          </div>
          <div key="back">
            {/* Content for the back of the card */}
            {Array.isArray(zine.images) &&
              zine.images.map((image, imageIndex) => (
                <img
                  key={imageIndex}
                  src={image.data}
                  alt={`Zine ${index + 1} Image ${imageIndex + 1}`}
                  onClick={() => flipCard(index)}
                />
              ))}
          </div>
        </ReactCardFlip>
      ))}
    </div>
  );
};

export default Zines;
