import { useState } from "react";

const AddZines = ({ onAdd }) => {
  const [text, setText] = useState("");

  const onSubmit = (e) => {
    e.preventDefault();
    if (!text) {
      alert("Please add a zine");
      return;
    }
    onAdd({ text });

    setText("");
  };

  return (
    <form className="add-zine" onSubmit={onSubmit}>
      <label for="title">Title:</label>
      <br />
      <input
        type="text"
        id="title"
        name="title"
        placeholder="Title of your zine"
        value={text}
        onChange={(e) => setText(e.target.value)}
        required
      ></input>
      <br />
      <label for="author">Author:</label>
      <input
        type="text"
        id="author"
        name="author"
        placeholder="Author of this zine"
        value={text}
        onChange={(e) => setText(e.target.value)}
        required
      ></input>
      <br />
      <label for="description">Description:</label>
      <textarea
        id="description"
        name="description"
        placeholder="Add a description for your zine here"
        onChange={(e) => setText(e.target.value)}
      ></textarea>
      <input type="submit" value="Upload Zine" required></input>
    </form>
  );
};

export default AddZines;
