import { useState } from "react";
import ReactCardFlip from "react-card-flip";

const Zines = ({ zines }) => {
  const [isFlipped, setIsFlipped] = useState(false);

  function flipCard() {
    setIsFlipped(!isFlipped);
  }

  return (
    <div>
      {zines.map((zine, index) => (
        <div key={index}>
          <ReactCardFlip flipDirection="horizontal" isFlipped={isFlipped}>
            <div key="front" onClick={flipCard}>
              {/* Content for the front of the card */}
              Click to flip
            </div>
            <div key="back">
              {/* Content for the back of the card */}
              {Array.isArray(zine.images) &&
                zine.images.map((image, imageIndex) => (
                  <img
                    key={imageIndex}
                    src={image.data}
                    alt={`Zine ${index + 1} Image ${imageIndex + 1}`}
                  />
                ))}
            </div>
          </ReactCardFlip>
          {/* {console.log("zine.images", zine.images)} */}
        </div>
      ))}
    </div>
  );
};

export default Zines;
