import { useState } from "react";

const AddZines = ({ onAdd }) => {
  const [text, setText] = useState("");

  const onSubmit = (e) => {
    e.preventDefault();
    if (!text) {
      alert("Please add a zine");
      return;
    }
    onAdd({ text });

    setText("");
  };

  return (
    <form className="add-zine" onSubmit={onSubmit}>
      <label>Zine:</label>
      <input
        type="text"
        placeholder="You can a description of your zine here"
        value={text}
        onChange={(e) => setText(e.target.value)}
      ></input>
      <input type="submit" value="Upload Zine"></input>
    </form>
  );
};

export default AddZines;
