import ImageSlider from "./ImageSlider";

const Zines = ({ zines }) => {
  const containerStyles = {
    width: "360px",
    height: "470px",
    margin: "0 auto",
  };

  return (
    <div>
      {zines.map((zine, index) => (
        <div key={index}>
          {Array.isArray(zine.images) && (
            <ImageSlider images={zine.images} containerStyles={containerStyles}>
              {zine.images.map((image, imageIndex) => (
                <div key={imageIndex}>
                  <img
                    key={imageIndex}
                    src={image.data}
                    alt={`Zine ${index + 1} Image ${imageIndex + 1}`}
                    onClick={() => goToSlide(slideIndex)}
                  />
                </div>
              ))}
            </ImageSlider>
          )}
          {/* {console.log("zine.images", zine.images)} */}
        </div>
      ))}
    </div>
  );
};

export default Zines;
