import { useState } from "react";

const AddZines = ({ onAdd }) => {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [description, setDescription] = useState("");

  const [images, setImages] = useState([]);
  const [imageData, setImageData] = useState([]);

  const handleImageChange = (e) => {
    const selectedImages = Array.from(e.target.files);
    setImages(selectedImages);
  };

  const handleUpload = () => {
    const promises = images.map((image) => {
      return new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.onload = (e) => {
          resolve({
            name: image.name,
            data: e.target.result,
          });
        };
        reader.readAsDataURL(image);
      });
    });

    Promise.all(promises)
      .then((data) => {
        setImageData(data);
        // Save data to JSON file (you may want to implement server-side logic for this)
        const jsonData = JSON.stringify(data, null, 2);
        const blob = new Blob([jsonData], { type: "application/json" });
        const url = URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = "imageData.json";
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
      })
      .catch((error) => {
        console.error("Error reading images:", error);
      });

    onAdd({ title, author, description });

    setTitle("");
    setAuthor("");
    setDescription("");
    setImages("");
  };

  return (
    <form className="add-zine" onSubmit={onSubmit}>
      <label htmlFor="title">Title:</label>
      <br />
      <input
        type="text"
        id="title"
        name="title"
        placeholder="Title of this zine"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
      ></input>
      <br />
      <label htmlFor="author">Author:</label>
      <br />
      <input
        type="text"
        id="author"
        name="author"
        placeholder="Author of this zine"
        value={author}
        onChange={(e) => setAuthor(e.target.value)}
        required
      ></input>
      <br />
      <label htmlFor="description">Description:</label>
      <br />
      <textarea
        id="description"
        name="description"
        placeholder="Add a description for your zine here"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      ></textarea>
      <br />
      <input
        type="file"
        id="images"
        name="images"
        accept="image/*"
        value={images}
        onChange={(e) => setImages(e.target.files[0])}
        required
        multiple
      ></input>
      <br />
      <input type="submit" value="Upload Zine"></input>
    </form>
  );
};

export default AddZines;
