import { useState, useEffect } from "react";

const ImageSlider = ({ images, children, onImageClick, containerStyles }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    if (currentIndex < 0) {
      setCurrentIndex(images.length - 1);
    } else if (currentIndex >= images.length) {
      setCurrentIndex(0);
    }
  }, [currentIndex, images]);

  const sliderStyles = {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    maxWidth: "auto",
    margin: "0 auto",
    // ...containerStyles,
  };

  const slideStyles = {
    // display: "flex",
    // flexDirection: "column",
    // alignItems: "center",
    // justifyContent: "center",

    width: "100%",
    height: "auto",
    borderRadius: "1em",
    border: "solid 5px",
    borderColor: "rgb(28, 28, 28)",
    boxShadow: "0 5px 0 rgb(45, 45, 61)",
    backgroundPosition: "center",
    backgroundSize: "cover",
    position: "relative",
    maxWidth: "400px",
    alignContent: "center",
    margin: "0 auto",
  };

  const leftArrowStyles = {
    position: "absolute",
    top: "50%",
    transform: "translate(0,-50%)",
    left: "0",
    fontSize: "45px",
    color: "rgb(210, 210, 255)",
    zIndex: 1,
    cursor: "pointer",
    fontWeight: "bold",
    textShadow:
      "-2px -2px 0 rgb(28, 28, 28), 2px -2px 0 rgb(28, 28, 28), -2px 2px 0 rgb(28, 28, 28), 2px 2px 0 rgb(28, 28, 28)",
  };

  const rightArrowStyles = {
    position: "absolute",
    top: "50%",
    transform: "translate(0,-50%)",
    right: "0",
    fontSize: "45px",
    color: "rgb(210, 210, 255)",
    zIndex: 1,
    cursor: "pointer",
    fontWeight: "bold",
    textShadow:
      "-2px -2px 0 rgb(28, 28, 28), 2px -2px 0 rgb(28, 28, 28), -2px 2px 0 rgb(28, 28, 28), 2px 2px 0 rgb(28, 28, 28)",
  };

  const dotsContainerStyles = {
    display: "flex",
    justifyContent: "center",
  };

  const dotStyles = {
    margin: "0 3px",
    cursor: "pointer",
    fontSize: "30px",
    fontWeight: "bold",
    color: "rgb(210, 210, 255)",
    textShadow:
      "-2px -2px 0 rgb(28, 28, 28), 2px -2px 0 rgb(28, 28, 28), -2px 2px 0 rgb(28, 28, 28), 2px 2px 0 rgb(28, 28, 28)",
  };

  const goToPrevious = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  const goToNext = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  const goToSlide = (imageIndex) => {
    setCurrentIndex(imageIndex);
  };

  return (
    <div style={sliderStyles}>
      <div style={leftArrowStyles} onClick={goToPrevious}>
        &lt;
      </div>
      <div style={rightArrowStyles} onClick={goToNext}>
        &gt;
      </div>
      <div style={slideStyles} onClick={onImageClick}>
        <img
          src={images[currentIndex].data}
          alt={`Slide ${currentIndex}`}
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            borderRadius: "1em",
          }}
        />
        {children}
      </div>
      <div style={dotsContainerStyles}>
        {images.map((slideIndex) => (
          <div
            key={slideIndex}
            style={dotStyles}
            onClick={() => goToSlide(slideIndex)}
          >
            &#x2022;
          </div>
        ))}
      </div>
    </div>
  );
};

export default ImageSlider;
