import { useState, useEffect } from "react";

const AddZines = ({ onAdd }) => {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [description, setDescription] = useState("");
  const [images, setImages] = useState([]);

  useEffect(() => {
    return () => {
      images.forEach((image) => URL.revokeObjectURL(image.preview));
    };
  }, [images]);

  const onSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append("title", title);
      formData.append("author", author);
      formData.append("description", description);

      images.forEach((image) => formData.append("images", image));

      const response = await fetch("/upload", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();

      onAdd(data);

      setTitle("");
      setAuthor("");
      setDescription("");
      setImages([]);
    } catch (error) {
      console.error("Error uploading zine:", error);
    }
  };

  const handleImageChange = (e) => {
    const files = e.target.files;

    const imagePreviews = Array.from(files).map((file) =>
      URL.createObjectURL(file)
    );

    setImages(
      files.map((file, index) => ({
        file,
        preview: imagePreviews[index],
      }))
    );
  };
  return (
    <form className="add-zine" onSubmit={onSubmit}>
      <label htmlFor="title">Title:</label>
      <br />
      <input
        type="text"
        id="title"
        name="title"
        placeholder="Title of this zine"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
      ></input>
      <br />
      <label htmlFor="author">Author:</label>
      <br />
      <input
        type="text"
        id="author"
        name="author"
        placeholder="Author of this zine"
        value={author}
        onChange={(e) => setAuthor(e.target.value)}
        required
      ></input>
      <br />
      <label htmlFor="description">Description:</label>
      <br />
      <textarea
        id="description"
        name="description"
        placeholder="Add a description for your zine here"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      ></textarea>
      <br />
      <label htmlFor="images">Images:</label>
      <br />
      <input
        type="file"
        id="images"
        name="images"
        accept="image/*"
        onChange={(e) => setImages(e.target.files)}
        multiple
        required
      ></input>
      <br />
      <input type="submit" value="Upload Zine"></input>
    </form>
  );
};

export default AddZines;
