import { useState } from "react";

const AddZines = ({ onAdd }) => {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState([]);

  const onSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <form className="add-zine" onSubmit={onSubmit}>
      <label htmlFor="title">Title:</label>
      <br />
      <input
        type="text"
        id="title"
        name="title"
        placeholder="Title of this zine"
        value={text}
        onChange={(e) => setTitle(e.target.value)}
        required
      ></input>
      <br />
      <label htmlFor="author">Author:</label>
      <br />
      <input
        type="text"
        id="author"
        name="author"
        placeholder="Author of this zine"
        value={text}
        onChange={(e) => setAuthor(e.target.value)}
        required
      ></input>
      <br />
      <label htmlFor="description">Description:</label>
      <br />
      <textarea
        id="description"
        name="description"
        placeholder="Add a description for your zine here"
        onChange={(e) => setDescription(e.target.value)}
      ></textarea>
      <br />
      <input
        type="file"
        id="image"
        name="image"
        accept="image/*"
        onChange={(e) => setImage(e.target.files[0])}
        required
      ></input>
      <br />
      <input type="submit" value="Upload Zine"></input>
    </form>
  );
};

export default AddZines;
